mapper.select:
		select m.* from users m left join video_ident v on m.id=v.user_id  where m.is_delete=0 
		
		    
             and v.add_time is not null 
             and v.is_delete=0   
        
        
           
             and v.add_time is null 
        
        
        
            
          and m.data_type=#{data_type}
         
        
           
          and m.data_type=#{dataType}
         
        
           
          and m.user_township=#{xzb}
         
        
            
          and m.user_township=#{user_township}
         
       
        
		and unix_timestamp(v.add_time) >= unix_timestamp(#{startTime})
		
		
		and unix_timestamp(v.add_time) <=  unix_timestamp(#{endTime})
		
		
		  
          and ( m.user_name  like '%${key}%' or  m.data_type  like '%${key}%'  or m.user_township  like '%${key}%' or m.user_idcard like '%${key}%' or m.user_village  like '%${key}%' )
         
		 
		order by m.add_time  desc

mapper.update:
update users
user_name=#{user_name} , user_township=#{user_township},user_village=#{user_village},data_type=#{data_type}, mobile=#{mobile} ,update_time=now()
where id=#{id} and user_idcard=#{user_idcard}

update users
user_name=#{user_name}, user_township=#{user_township},user_village=#{user_village},data_type=#{data_type}, mobile=#{mobile},update_time=now()
where user_idcard=#{user_idcard}

		update users  
		  
         user_name=#{user_name} 
         
       where id=#{id} and user_idcard=#{user_idcard}
