mapper.select:
 
    select r.* ,u.user_name as user_name ,u.user_idcard as user_idcard,u.user_village as user_village,u.user_township as user_township,u.mobile as user_mobile,u.img_url as user_img from video_ident r 
	   LEFT JOIN users u on u.id=r.user_id

	 where r.is_delete=0    and u.is_delete=0 and r.id= #{id}

